import React, { useState, useEffect } from 'react';

const PROJECTS = [
  {
    id: 'crypto',
    title: "Scalable Crypto Market Pipeline", 
    subtitle: "Hybrid ELT Architecture",
    stack: ["Airflow", "DuckDB", "dbt", "PostgreSQL", "Docker"], 
    summary: "An end-to-end data platform that ingests 10M+ daily market events. Features a decoupled compute layer using DuckDB to pre-process JSON data, significantly reducing warehouse compute costs.",
    highlights: [
      "Vectorized pre-processing with DuckDB", 
      "Idempotent DAG design with automated recovery", 
      "Star Schema modeling with dbt-core"
    ],
    metric: "99.9%",
    metricLabel: "Uptime Reliability",
    repo: "https://github.com/MohdIllham/crypto-etl-pipeline", 
    color: "#6366f1"
  },
  {
    id: 'cv-tool',
    title: "Image Scaling & CSV Extractor", 
    subtitle: "Computer Vision & Automation",
    stack: ["Python", "OpenCV", "NumPy", "Tkinter"],
    summary: "A GUI-based automation tool designed to extract scaled coordinates from red-colored regions in images. Engineered to digitize manual data points with independent X/Y scaling and HSV color filtering.",
    highlights: [
      "Dual-range HSV Red Color Detection", 
      "Independent X/Y Coordinate Scaling", 
      "Automated CSV Export Workflow"
    ],
    metric: "< 1s",
    metricLabel: "Processing Time / Image",
    repo: "https://github.com/MohdIllham/Gamma-Ray-Detection", 
    color: "#f43f5e"
  }
];


const ArchitectureFlow = ({ projectId }: { projectId: string }) => {
  const [activeStep, setActiveStep] = useState<number | null>(null);
  const [progress, setProgress] = useState(0);

  // Auto-advance progress for Gamma project
  useEffect(() => {
    if (projectId === 'cv-tool') {
      const interval = setInterval(() => {
        setProgress(prev => (prev >= 100 ? 0 : prev + 25));
      }, 2000);
      return () => clearInterval(interval);
    }
  }, [projectId]);

  // Crypto Project Architecture
  if (projectId === 'crypto') {
    return (
      <div className="bg-slate-900 rounded-2xl p-6 md:p-10 my-8 border border-slate-800 shadow-2xl overflow-x-auto">
        <div className="flex items-center justify-between min-w-[650px] gap-4">
          {/* Source */}
          <div className="flex flex-col items-center gap-3 group">
            <div className="w-14 h-14 rounded-xl bg-slate-800 border border-slate-700 flex items-center justify-center text-amber-400 group-hover:scale-110 transition-transform duration-300">
              <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                <path d="M12 2v20M17 5H9.5a3.5 3.5 0 0 0 0 7h5a3.5 3.5 0 0 1 0 7H6"/>
              </svg>
            </div>
            <span className="text-[10px] font-bold text-slate-500 uppercase tracking-wider text-center group-hover:text-amber-400 transition-colors">
              CoinGecko API
            </span>
          </div>

          <div className="flex-1 h-px bg-slate-800 relative">
            <div className="absolute -top-3 left-1/2 -translate-x-1/2 bg-slate-900 px-2 text-[8px] text-slate-500 font-bold uppercase">Requests</div>
          </div>

          {/* Compute */}
          <div className="flex flex-col items-center gap-3 group">
            <div className="w-20 h-20 rounded-2xl bg-orange-500/10 border border-orange-500/50 flex items-center justify-center text-orange-400 shadow-[0_0_30px_rgba(249,115,22,0.15)] group-hover:shadow-[0_0_40px_rgba(249,115,22,0.3)] group-hover:scale-105 transition-all duration-300">
              <svg width="32" height="32" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2.5">
                <path d="M21 16V8a2 2 0 0 0-1-1.73l-7-4a2 2 0 0 0-2 0l-7 4A2 2 0 0 0 3 8v8a2 2 0 0 0 1 1.73l7 4a2 2 0 0 0 2 0l7-4A2 2 0 0 0 21 16z"/>
                <polyline points="3.27 6.96 12 12.01 20.73 6.96"/>
                <line x1="12" y1="22.08" x2="12" y2="12"/>
              </svg>
            </div>
            <span className="text-[10px] font-black text-orange-400 uppercase tracking-widest text-center italic group-hover:text-orange-300 transition-colors">
              DuckDB Compute
            </span>
          </div>

          <div className="flex-1 h-px bg-slate-800 relative">
            <div className="absolute -top-3 left-1/2 -translate-x-1/2 bg-slate-900 px-2 text-[8px] text-slate-500 font-bold uppercase">Vectorized Load</div>
          </div>

          {/* Storage */}
          <div className="flex flex-col items-center gap-3 group">
            <div className="w-16 h-16 rounded-xl bg-slate-800 border border-slate-700 flex items-center justify-center text-blue-400 group-hover:scale-110 transition-transform duration-300">
              <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                <ellipse cx="12" cy="5" rx="9" ry="3"/>
                <path d="M3 5V19A9 3 0 0 0 21 19V5"/>
                <path d="M3 12A9 3 0 0 0 21 12"/>
              </svg>
            </div>
            <span className="text-[10px] font-bold text-slate-500 uppercase tracking-wider text-center group-hover:text-blue-400 transition-colors">
              PostgreSQL
            </span>
          </div>

          <div className="flex-1 h-px bg-slate-800 relative">
            <div className="absolute -top-3 left-1/2 -translate-x-1/2 bg-slate-900 px-2 text-[8px] text-slate-500 font-bold uppercase">dbt Transformation</div>
          </div>

          {/* Marts */}
          <div className="flex flex-col items-center gap-3 group">
            <div className="w-16 h-16 rounded-xl bg-indigo-500/20 border border-indigo-500/50 flex items-center justify-center text-indigo-400 group-hover:scale-110 transition-transform duration-300">
              <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                <path d="M3 3v18h18"/>
                <path d="M18 17V9"/>
                <path d="M13 17V5"/>
                <path d="M8 17v-3"/>
              </svg>
            </div>
            <span className="text-[10px] font-bold text-indigo-400 uppercase tracking-wider text-center group-hover:text-indigo-300 transition-colors">
              Data Marts
            </span>
          </div>
        </div>
        
        {/* Crypto Pipeline Status */}
        <div className="mt-8 pt-6 border-t border-slate-800">
          <div className="flex items-center justify-between text-xs">
            <div className="flex items-center gap-2">
              <div className="w-2 h-2 rounded-full bg-green-500 animate-pulse"></div>
              <span className="text-slate-400">Live Pipeline</span>
            </div>
            <div className="text-slate-500">
              Processing: <span className="text-amber-400 font-bold">10M+ events/day</span>
            </div>
          </div>
        </div>
      </div>
    );
  }

  // Gamma (Computer Vision) Project Architecture
  if (projectId === 'cv-tool') {
    const steps = [
      {
        id: 1,
        icon: "üñºÔ∏è",
        title: "Image Input",
        description: "Load JPG/PNG images with batch processing support",
        details: "Supports multiple image formats with automatic validation and preprocessing",
        technologies: ["OpenCV imread", "Tkinter FileDialog", "Batch Processing"],
        color: "from-rose-400 to-pink-500",
        time: "< 100ms"
      },
      {
        id: 2,
        icon: "üé®",
        title: "HSV Detection",
        description: "Dual-range HSV filtering for precise red color detection",
        details: "Uses two HSV ranges to capture different shades of red with adjustable sensitivity",
        technologies: ["OpenCV inRange", "HSV Color Space", "Mask Operations", "Morphology"],
        color: "from-pink-500 to-fuchsia-500",
        time: "~ 200ms"
      },
      {
        id: 3,
        icon: "üìè",
        title: "Coordinate Scaling",
        description: "Independent X/Y scaling with custom transformation",
        details: "Applies separate scaling factors to X and Y axes, preserving spatial relationships",
        technologies: ["NumPy Arrays", "Linear Transformation", "Custom Scaling Matrix"],
        color: "from-fuchsia-500 to-purple-500",
        time: "~ 50ms"
      },
      {
        id: 4,
        icon: "üìä",
        title: "CSV Export",
        description: "Automated CSV generation with metadata",
        details: "Exports processed coordinates with timestamps, configuration data, and image metadata",
        technologies: ["Pandas DataFrame", "CSV Writer", "Automated Naming", "Metadata"],
        color: "from-purple-500 to-indigo-500",
        time: "~ 100ms"
      }
    ];

    return (
      <div className="bg-slate-900 rounded-2xl p-6 md:p-10 my-8 border border-slate-800 shadow-2xl">
        {/* Progress Bar */}
        <div className="relative mb-8">
          <div className="h-2 bg-slate-800 rounded-full overflow-hidden">
            <div 
              className="h-full bg-gradient-to-r from-rose-500 via-fuchsia-500 to-indigo-500 transition-all duration-1000 ease-out rounded-full"
              style={{ width: `${progress}%` }}
            />
          </div>
          <div className="flex justify-between mt-2">
            {steps.map((step, index) => (
              <div 
                key={step.id}
                className={`text-[10px] font-bold ${progress >= (index * 25) ? 'text-white' : 'text-slate-500'}`}
              >
                Step {index + 1}
              </div>
            ))}
          </div>
        </div>

        {/* Main Flow */}
        <div className="flex items-center justify-between min-w-[650px] gap-4 mb-8 overflow-x-auto">
          {steps.map((step, index) => {
            const isActive = progress >= (index * 25);
            
            return (
              <React.Fragment key={step.id}>
                <button
                  onClick={() => setActiveStep(activeStep === index ? null : index)}
                  className={`flex flex-col items-center gap-3 transition-all duration-500 ${
                    activeStep === index ? 'scale-110' : 'hover:scale-105'
                  }`}
                >
                  {/* Step Circle */}
                  <div className="relative">
                    <div className={`
                      w-20 h-20 rounded-full flex items-center justify-center text-2xl
                      transition-all duration-700
                      ${isActive 
                        ? `bg-gradient-to-br ${step.color} shadow-[0_0_40px_rgba(244,63,94,0.4)] text-white` 
                        : 'bg-slate-800 border border-slate-700 text-rose-400'
                      }
                    `}>
                      {step.icon}
                      
                      {/* Active Ring Animation */}
                      {isActive && (
                        <div className="absolute inset-0 rounded-full border-4 border-rose-400/30 animate-ping" />
                      )}
                    </div>
                    
                    {/* Time Badge */}
                    <div className={`absolute -bottom-1 -right-1 px-2 py-1 rounded text-[8px] font-bold transition-all ${
                      isActive ? 'bg-white text-slate-900' : 'bg-slate-800 text-slate-400 border border-slate-700'
                    }`}>
                      {step.time}
                    </div>
                    
                    {/* Step Number */}
                    <div className={`absolute -top-2 -right-2 w-6 h-6 rounded-full flex items-center justify-center text-[10px] font-black transition-all ${
                      isActive ? 'bg-rose-500 text-white' : 'bg-slate-700 text-slate-400'
                    }`}>
                      {index + 1}
                    </div>
                  </div>
                  
                  <div className="text-center max-w-[100px]">
                    <div className={`text-[10px] font-bold uppercase tracking-wider transition-colors ${
                      isActive ? 'text-white' : 'text-rose-400'
                    }`}>
                      {step.title}
                    </div>
                    <div className="text-[8px] text-slate-500 mt-1">
                      {step.description}
                    </div>
                  </div>
                </button>
                
                {/* Connector */}
                {index < steps.length - 1 && (
                  <div className="flex-1 h-px bg-gradient-to-r from-slate-800 via-slate-700 to-slate-800 relative">
                    <div className="absolute -top-3 left-1/2 -translate-x-1/2 bg-slate-900 px-2 text-[8px] text-slate-500 font-bold uppercase">
                      ‚Üí
                    </div>
                  </div>
                )}
              </React.Fragment>
            );
          })}
        </div>

        {/* Details Panel */}
        {activeStep !== null && (
          <div className="mt-8 p-6 bg-gradient-to-r from-slate-800/50 to-slate-900/50 rounded-xl border border-slate-700 animate-fadeIn">
            <div className="flex justify-between items-start mb-4">
              <div>
                <div className="flex items-center gap-3 mb-2">
                  <div className={`w-10 h-10 rounded-full bg-gradient-to-br ${steps[activeStep].color} flex items-center justify-center text-xl`}>
                    {steps[activeStep].icon}
                  </div>
                  <div>
                    <h4 className="text-lg font-bold text-white">{steps[activeStep].title}</h4>
                    <div className="text-sm text-rose-300">{steps[activeStep].time}</div>
                  </div>
                </div>
                <p className="text-sm text-slate-300 mb-4">{steps[activeStep].details}</p>
              </div>
              <button
                onClick={() => setActiveStep(null)}
                className="text-slate-400 hover:text-white text-2xl transition-colors"
              >
                √ó
              </button>
            </div>
            
            <div className="mb-4">
              <div className="text-xs text-slate-400 mb-2 font-bold">Technologies Used:</div>
              <div className="flex flex-wrap gap-2">
                {steps[activeStep].technologies.map((tech) => (
                  <span 
                    key={tech}
                    className="px-3 py-1.5 bg-rose-500/20 text-rose-300 rounded-lg text-xs font-medium hover:bg-rose-500/30 transition-colors cursor-default"
                  >
                    {tech}
                  </span>
                ))}
              </div>
            </div>
            
            <div className="pt-4 border-t border-slate-700">
              <div className="text-xs text-slate-400">
                <span className="font-bold">Step {activeStep + 1} of {steps.length}</span> ‚Ä¢ 
                Click any step to view details ‚Ä¢ 
                <span className="ml-2 text-rose-300">Processing time: {steps[activeStep].time}</span>
              </div>
            </div>
          </div>
        )}

        {/* Controls */}
        <div className="flex flex-wrap justify-center gap-3 mt-6">
          <button
            onClick={() => setProgress(prev => Math.max(0, prev - 25))}
            className="px-4 py-2 bg-slate-800 hover:bg-slate-700 rounded-lg text-sm text-slate-300 transition-colors flex items-center gap-2"
          >
            <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <polyline points="15 18 9 12 15 6"/>
            </svg>
            Previous Step
          </button>
          <button
            onClick={() => setProgress(prev => Math.min(100, prev + 25))}
            className="px-4 py-2 bg-gradient-to-r from-rose-600 to-fuchsia-600 hover:from-rose-500 hover:to-fuchsia-500 rounded-lg text-sm text-white transition-all flex items-center gap-2"
          >
            Next Step
            <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <polyline points="9 18 15 12 9 6"/>
            </svg>
          </button>
          <button
            onClick={() => setProgress(0)}
            className="px-4 py-2 bg-slate-700 hover:bg-slate-600 rounded-lg text-sm text-slate-300 transition-colors"
          >
            Reset Animation
          </button>
          <button
            onClick={() => setActiveStep(activeStep === null ? 0 : null)}
            className="px-4 py-2 bg-slate-800 hover:bg-slate-700 rounded-lg text-sm text-rose-300 transition-colors"
          >
            {activeStep === null ? 'Show Details' : 'Hide Details'}
          </button>
        </div>
      </div>
    );
  }

  return null;
};



export default function App() {
  const [activeTab, setActiveTab] = useState('projects');

  const skills = [
